                    @Override
                    public void onCompleted() {
                        Log.d("lll", Thread.currentThread().getName() + "  ->onCompleted");
                    }

                    @Override
                    public void onError(Throwable e) {

                    }

                    @Override
                    public void onNext(String s) {
                        Log.d("lll", Thread.currentThread().getName() + "  ->next:" + s);
                    }
                });
